import React, { Component } from 'react';
import { View, Text, StyleSheet, FlatList } from 'react-native';

const randomAge = {}

export const ListScreen = () => {
    const friends = [
        { name: 'Friend #1' },
        { name: 'Friend #2' },
        { name: 'Friend #3' },
        { name: 'Friend #4' },
        { name: 'Friend #5' },
        { name: 'Friend #6' },
        { name: 'Friend #7' },
    ]
    return (
        <FlatList
            horizontal={false}
            showsHorizontalScrollIndicator={true}
            showsVerticalScrollIndicator={false}
            data={friends}
            renderItem={({item}) => {
                return (
                    <Text style={styles.textStyle}>{item.name}</Text>
                )
            }}
            keyExtractor={item => item.name}
        />
    ) 
}

const styles = StyleSheet.create({
    textStyle: {
        marginVertical: 50,
        marginRight: 15,
    }
});

const getRandomId = () => {
    const ids = [];
    const innerReturn = () => {
        let value = () => String(Math.floor(Math.random())*5);
        if (ids.includes(value)) {
            console.log('repetido');
            console.log(ids);
            console.log(value)
            innerReturn(); 
        };
        ids.push(value);
        console.log('addded')
        console.log(ids);
        return value;
    }
    return innerReturn;
}
