import React, { Component } from 'react';
import { View, Text, StyleSheet, FlatList } from 'react-native';

const randomAge = {}

export const ListScreen = () => {
    const friends = [
        { name: 'Friend #1' },
        { name: 'Friend #2' },
        { name: 'Friend #3' },
        { name: 'Friend #4' },
        { name: 'Friend #5' },
        { name: 'Friend #6' },
        { name: 'Friend #7' },
    ]
    return (
        <FlatList
            horizontal={false}
            showsHorizontalScrollIndicator={true}
            showsVerticalScrollIndicator={false}
            data={friends}
            renderItem={({item}) => {
                return (
                    <Text style={styles.textStyle}>{item.name}</Text>
                )
            }}
            key={randomIdGenerator()}
        />
    ) 
}

const styles = StyleSheet.create({
    textStyle: {
        marginVertical: 50,
        marginRight: 15,
    }
});

const getRandomId = () => {
    const ids = [];
    const innerReturn = () => {
        let value;
        let count = 0;
        do {
            value = String(Math.random());
            count += 1;
        } while (ids.includes(value) && count < 5000);
        ids.push(value);
        return value;
    }
    return innerReturn;
}

const randomIdGenerator = getRandomId();
